insert into University([name],[group], [shortDescription], [longDescription], [examInfo], [email], [phoneNumber],[address],[city_id]) values (
'Facultatea de Limbi si Literaturi Străine', 
'LLS-UNIBUC', 
'Facultatea de Limbi si Literaturi Straine, una dintre cele mai mari si mai importante facultati din cadrul Universitatii din Bucuresti, are o bogata traditie in predarea limbilor si literaturilor straine. Cei peste 150 de ani de istorie a Universitatii din Bucuresti sunt, totodata, o istorie mereu ascendenta a predarii limbilor si literaturilor straine.',
